import React from 'react';
import css from './styles.module.scss';
import {
    CSSTransition,
    TransitionGroup,
} from 'react-transition-group';
import dogs from "../../dogs.jpg";
import ReactDOM from "react-dom";
import App from "../../App";

export class _Login1 extends React.Component{
    render(){
        return(
            <div >

            </div>
        );
    }
}

export class _Login extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), beg_date: new Date(), loaded: false,      isLoginOpen: true,
            isRegisterOpen: false};
    }

    componentDidMount() {
        this.state.loaded = true
        this.timerID = setInterval(
            () => this.tick(),
            2000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    render() {
        if (!this.state.loaded) {
            return (

                <div className="wrap">
                    <div className="loading">
                        <div className="bounceball"></div>
                        <div className="text">LOADING</div>
                    </div>
                </div>
            );
        }
        sleep(1000)

        return (
            <div className="App">
                <header className="App-header">
                    <div className="poster">


                    </div>
                    <div className="root-container">      {this.state.isLoginOpen && <LoginBox/>}
                        {this.state.isRegisterOpen && <RegisterBox/>}</div>


                    <div>
                        <span  align="center" className="dropbtn">
                               <a className="dropbtn" align="center" href="login" target="_blank"
                                  rel="nofollow noopener">Обратная связь</a>
                            </span>
                    </div>

                </header>
            </div>
        );

    }
}

