import React from 'react';
import './App.css';
import doggo from "./doggo.jpg"
import ReactDOM from 'react-dom';

const sleep = (ms) =>
    new Promise(resolve => setTimeout(resolve, ms));

function FormattedDate(props) {
    return <h2>It is {props.date.toLocaleTimeString()}.</h2>;
}

function myFunction(x) {
    x.classList.toggle("fa-thumbs-down");
}

class Loader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), beg_date: new Date(), loaded: false,      isLoginOpen: true,
            isRegisterOpen: false};
    }

    componentDidMount() {
        this.state.loaded = true
        this.timerID = setInterval(
            () => this.tick(),
            2000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    showLoginBox() {
        this.setState({isLoginOpen: true, isRegisterOpen: false});
    }

    showRegisterBox() {
        this.setState({isRegisterOpen: true, isLoginOpen: false});
    }

    render() {
        if (!this.state.loaded) {
            return (

                <div className="wrap">
                    <div className="loading">
                        <div className="bounceball"></div>
                        <div className="text">LOADING</div>
                    </div>
                </div>
            );
        }
        sleep(1000)

        return (
            <div className="App">
                <header className="App-header">
                    <div className="poster">


                    </div>
                    <div className="root-container">

                        <div className="box-controller">
                            <div
                                className={"controller " + (this.state.isLoginOpen
                                    ? "selected-controller"
                                    : "")}
                                onClick={this
                                    .showLoginBox
                                    .bind(this)}>
                                Login
                            </div>
                            <div
                                className={"controller " + (this.state.isRegisterOpen
                                    ? "selected-controller"
                                    : "")}
                                onClick={this
                                    .showRegisterBox
                                    .bind(this)}>
                                Register
                            </div>
                        </div>

                        <FadeTransition isOpen={this.state.isLoginOpen} duration={500}>
                            <div className="box-container">
                                <LoginBox/>
                            </div>
                        </FadeTransition>
                        <FadeTransition isOpen={this.state.isRegisterOpen} duration={500}>
                            <div className="box-container">
                                <RegisterBox/>
                            </div>
                        </FadeTransition>

                    </div>


                    <div>
                        <span  align="center" className="dropbtn">
                               <a className="dropbtn" align="center" href="login" target="_blank"
                                  rel="nofollow noopener">Обратная связь</a>
                            </span>
                    </div>

                </header>
            </div>
        );

    }
}



function App() {
    return (
        <div>
            <Loader/>
        </div>
    );
}

export default App;
ReactDOM.render(
    <App />, document.getElementById("root"));
