import React from 'react';
import './App.css';
import doggo from "./doggo.jpg"
import ReactDOM from 'react-dom';
import TransitionGroup from "react-transition-group";
import Transition from "react-transition-group";

const sleep = (ms) =>
    new Promise(resolve => setTimeout(resolve, ms));

function FormattedDate(props) {
    return <h2>It is {props.date.toLocaleTimeString()}.</h2>;
}

function myFunction(x) {
    x.classList.toggle("fa-thumbs-down");
}

class Loader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), beg_date: new Date(), loaded: false,      isLoginOpen: true,
            isRegisterOpen: false};
    }

    showLoginBox() {
        this.setState({isLoginOpen: true, isRegisterOpen: false});
    }

    showRegisterBox() {
        this.setState({isRegisterOpen: true, isLoginOpen: false});
    }

    componentDidMount() {
        this.state.loaded = true
        this.timerID = setInterval(
            () => this.tick(),
            2000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    render() {
        if (!this.state.loaded) {
            return (

                <div className="wrap">
                    <div className="loading">
                        <div className="bounceball"></div>
                        <div className="text">LOADING</div>
                    </div>
                </div>
            );
        }
        sleep(1000)

        return (
            <div className="App">
                <header className="App-header">
                    <div className="poster">

                        <div className="root-container">

                            <div className="box-controller">
                                <div
                                    className={"controller " + (this.state.isLoginOpen
                                        ? "selected-controller"
                                        : "")}
                                    onClick={this
                                        .showLoginBox
                                        .bind(this)}>
                                    Login
                                </div>
                                <div
                                    className={"controller " + (this.state.isRegisterOpen
                                        ? "selected-controller"
                                        : "")}
                                    onClick={this
                                        .showRegisterBox
                                        .bind(this)}>
                                    Register
                                </div>
                            </div>

                            <FadeTransition isOpen={this.state.isLoginOpen} duration={500}>
                                <div className="box-container">
                                    <LoginBox/>
                                </div>
                            </FadeTransition>
                            <FadeTransition isOpen={this.state.isRegisterOpen} duration={500}>
                                <div className="box-container">
                                    <RegisterBox/>
                                </div>
                            </FadeTransition>

                        </div>

                    </div>


                </header>
            </div>
        );

    }
}

class FadeTransition extends React.Component {

    constructor(props) {
        super(props);
        //Default Style
        this.defaultStyle = {
            transitionProperty: "opacity",
            transition: `${this.props.duration}ms ease-in-out`
        };
        this.transitionStyle = {
            entering: {
                opacity: "0"
            },
            entered: {
                opacity: "1",
                transitionDelay: "700ms"
            },
            exiting: {
                opacity: "0"
            },
            exited: {
                opacity: "0"
            }
        };
    }

    render() {

        return (
            <Transition in={this.props.isOpen} timeout={this.props.duration}>
                {(state) => {
                    if (state == "exited")
                        return null;
                    return React
                        .Children
                        .map(this.props.children, (child, idx) => {
                            return React.cloneElement(child, {
                                style: Object.assign({}, this.defaultStyle, this.transitionStyle[state])
                            });
                        });
                }}
            </Transition>
        );

    }

}

class LoginBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    submitLogin(e) {}

    render() {
        return (
            <div className="inner-container">
                <div className="header">
                    Login
                </div>
                <div className="box">

                    <div className="input-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            name="username"
                            className="login-input"
                            placeholder="Username"/>
                    </div>

                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            name="password"
                            className="login-input"
                            placeholder="Password"/>
                    </div>

                    <button
                        type="button"
                        className="login-btn"
                        onClick={this
                            .submitLogin
                            .bind(this)}>Login</button>

                </div>
            </div>
        );
    }

}

function pop(props) {
    return
}

class RegisterBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {
            username: "",
            email: "",
            password: "",
            errors: [],
            pwdState: null
        };
    }

    showValidationErr(elm, msg) {
        this.setState((prevState) => ({
            errors: [
                ...prevState.errors, {
                    elm,
                    msg
                }
            ]
        }));
    }

    clearValidationErr(elm) {
        this.setState((prevState) => {
            let newArr = [];
            for (let err of prevState.errors) {
                if (elm != err.elm) {
                    newArr.push(err);
                }
            }
            return {errors: newArr};
        });
    }

    onUsernameChange(e) {
        this.setState({username: e.target.value});
        this.clearValidationErr("username");
    }

    onEmailChange(e) {
        this.setState({email: e.target.value});
        this.clearValidationErr("email");
    }

    onPasswordChange(e) {
        this.setState({password: e.target.value});
        this.clearValidationErr("password");

        this.setState({pwdState: "weak"});
        if (e.target.value.length > 8) {
            this.setState({pwdState: "medium"});
        } else if (e.target.value.length > 12) {
            this.setState({pwdState: "strong"});
        }

    }

    openPopup(e) {
        console.log("Hello world!");
    }

    submitRegister(e) {

        console.log(this.state);

        if (this.state.username == "") {
            this.showValidationErr("username", "Username Cannot be empty!");
        }
        if (this.state.email == "") {
            this.showValidationErr("email", "Email Cannot be empty!");
        }
        if (this.state.password == "") {
            this.showValidationErr("password", "Password Cannot be empty!");
        }

    }

    render() {

        let usernameErr = null,
            passwordErr = null,
            emailErr = null;

        for (let err of this.state.errors) {
            if (err.elm == "username") {
                usernameErr = err.msg;
            }
            if (err.elm == "password") {
                passwordErr = err.msg;
            }
            if (err.elm == "email") {
                emailErr = err.msg;
            }
        }

        let pwdWeak = false,
            pwdMedium = false,
            pwdStrong = false;

        if (this.state.pwdState == "weak") {
            pwdWeak = true;
        } else if (this.state.pwdState == "medium") {
            pwdWeak = true;
            pwdMedium = true;
        } else if (this.state.pwdState == "strong") {
            pwdWeak = true;
            pwdMedium = true;
            pwdStrong = true;
        }

        return (
            <div className="inner-container">
                <div className="header">
                    Register
                </div>
                <div className="box">

                    <div className="input-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            name="username"
                            className="login-input"
                            placeholder="Username"
                            onChange={this
                                .onUsernameChange
                                .bind(this)}/>
                        <small className="danger-error">{usernameErr
                            ? usernameErr
                            : ""}</small>
                    </div>

                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input
                            type="text"
                            name="email"
                            className="login-input"
                            placeholder="Email"
                            onChange={this
                                .onEmailChange
                                .bind(this)}/>
                        <small className="danger-error">{emailErr
                            ? emailErr
                            : ""}</small>
                    </div>

                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            name="password"
                            className="login-input"
                            placeholder="Password"
                            onChange={this
                                .onPasswordChange
                                .bind(this)}/>
                        <small className="danger-error">{passwordErr
                            ? passwordErr
                            : ""}</small>

                        {this.state.password && <div className="password-state">
                            <div
                                className={"pwd pwd-weak " + (pwdWeak
                                    ? "show"
                                    : "")}></div>
                            <div
                                className={"pwd pwd-medium " + (pwdMedium
                                    ? "show"
                                    : "")}></div>
                            <div
                                className={"pwd pwd-strong " + (pwdStrong
                                    ? "show"
                                    : "")}></div>
                        </div>}

                    </div>

                    <button
                        type="button"
                        className="login-btn"
                        onHover={this
                            .openPopup
                            .bind(this)}
                        onClick={this
                            .openPopup
                            .bind(this)}>Register</button>

                </div>
            </div>

        );

    }

}

