import React from 'react';
import './App.css';
import doggo from "./doggo.jpg"
import ReactDOM from 'react-dom';

const sleep = (ms) =>
    new Promise(resolve => setTimeout(resolve, ms));

function FormattedDate(props) {
    return <h2>It is {props.date.toLocaleTimeString()}.</h2>;
}

function myFunction(x) {
    x.classList.toggle("fa-thumbs-down");
}

class Loader extends React.Component {
    constructor(props) {
        super(props);
        this.state = {date: new Date(), beg_date: new Date(), loaded: false,      isLoginOpen: true,
            isRegisterOpen: false};
    }

    componentDidMount() {
        this.state.loaded = true
        this.timerID = setInterval(
            () => this.tick(),
            2000
        );
    }

    componentWillUnmount() {
        clearInterval(this.timerID);
    }

    tick() {
        this.setState({
            date: new Date()
        });
    }

    handle_login = (e, data) => {
        e.preventDefault();
        fetch('http://localhost:8000/token-auth/', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then(res => res.json())
            .then(json => {
                localStorage.setItem('token', json.token);
                this.setState({
                    logged_in: true,
                    displayed_form: '',
                    username: json.user.username
                });
            });
    };

    handle_logout = () => {
        localStorage.removeItem('token');
        this.setState({ logged_in: false, username: '' });
    };

    render() {
        if (!this.state.loaded) {
            return (

                <div className="wrap">
                    <div className="loading">
                        <div className="bounceball"></div>
                        <div className="text">LOADING</div>
                    </div>
                </div>
            );
        }
        sleep(1000)

        return (
            <div className="App">
                <header className="App-header">
                    <div className="poster">


                    </div>
                    <div className="root-container"> {this.state.isLoginOpen && <LoginBox/>}
                        </div>
                    <div className="root-container">
                        {this.state.isRegisterOpen && <RegisterBox/>}</div>
                </header>
            </div>
        );

    }
}

class LoginBox extends React.Component {
    state = {
        username: "",
        password: ""
    };

    constructor(props) {
        super(props);
        this.state = {};
    }

    submitLogin(e) {}

    handle_change = e => {
        const name = e.target.name;
        const value = e.target.value;
        this.setState(prevstate => {
            const newState = { ...prevstate };
            newState[name] = value;
            return newState;
        });
    };

    render() {
        return (
            <form onSubmit={e => this.props.handle_login(e, this.state)}>
                <h4>Log In</h4>
                <label htmlFor="username">Username</label>
                <input
                    type="text"
                    name="username"
                    value={this.state.username}
                    onChange={this.handle_change}
                />
                <label htmlFor="password">Password</label>
                <input
                    type="password"
                    name="password"
                    value={this.state.password}
                    onChange={this.handle_change}
                />
                <input type="submit" />
            </form>
        );
    }

}

class RegisterBox extends React.Component {

    constructor(props) {
        super(props);
        this.state = {};
    }

    submitRegister(e) {}

    render() {
        return (
            <div className="inner-container">
                <div className="header">
                    Register
                </div>
                <div className="box">

                    <div className="input-group">
                        <label htmlFor="username">Username</label>
                        <input
                            type="text"
                            name="username"
                            className="login-input"
                            placeholder="Username"/>
                    </div>

                    <div className="input-group">
                        <label htmlFor="email">Email</label>
                        <input type="text" name="email" className="login-input" placeholder="Email"/>
                    </div>

                    <div className="input-group">
                        <label htmlFor="password">Password</label>
                        <input
                            type="password"
                            name="password"
                            className="login-input"
                            placeholder="Password"/>
                    </div>
                    <button
                        type="button"
                        className="login-btn"
                        onClick={this
                            .submitRegister
                            .bind(this)}>Register</button>
                </div>
            </div>
        );
    }
}

function App() {
    return (
        <div>
            <Loader/>
        </div>
    );
}

export default App;

ReactDOM.render(
    <App />, document.getElementById("root"));
