import React from 'react';
import css from './styles.module.scss';
import {
    CSSTransition,
    TransitionGroup,
} from 'react-transition-group';
import dogs from "../../dogs.jpg";

export class _Login extends React.Component{
    render(){
        const button = document.querySelector('.btn')
        const form   = document.querySelector('.form')

        button.addEventListener('click', function() {
            form.classList.add('form--no')
        });
        return(
            <div >
                <img src={dogs} className="background" alt="dogs"/>
                <div className="login-window">
                <div className="log-form">
                    <h2>Login to your account</h2>
                    <form>
                        <input type="text" title="username" placeholder="username"/>
                        <input type="password" title="username" placeholder="password"/>
                        <button type="submit" className="btn">Log In</button>
                        <button type="submit" className="btn">Sign Up</button>
                        <a className="forgot" href="#">Forgot Username?</a>
                    </form>
                </div>
                </div>
                <div className="user">
                    <header className="user__header">
                        <img src="https://s3-us-west-2.amazonaws.com/s.cdpn.io/3219/logo.svg" alt=""/>
                        <h1 className="user__title">A lightweight and simple sign-up form</h1>
                    </header>

                    <form className="form">
                        <div className="form__group">
                            <input type="text" placeholder="Username" className="form__input"/>
                        </div>

                        <div className="form__group">
                            <input type="email" placeholder="Email" className="form__input"/>
                        </div>

                        <div className="form__group">
                            <input type="password" placeholder="Password" className="form__input"/>
                        </div>

                        <button className="btn" type="button">Register</button>
                    </form>
                </div>
            </div>

        );
    }
}
