    state = {
        isActive:false  }

    handleShow = ()=>{
        this.setState({
            isActive: true      })
    }

    handleHide = () =>{
        this.setState({
            isActive: false      })
    }

    render(){

        return(
            <div>
                <h1 className='Title_memes'> На этой страничке представлены все самые новые мемы про пёселей! </h1>
                <div>
                    {this.state.isActive ? <h1>Hello React</h1> : null }             <button onClick={this.handleShow}>Show</button>
                    <button onClick={this.handleHide}>Hide</button>
                </div>
            </div>

        );
    }
}
